function base64Encode(str) {
  return btoa(unescape(encodeURIComponent(str)))
}

export default {
  name: 'SvgIcon',
  props: {
    name: {
      type: String,
      required: true
    },
    color: {
      type: String,
      default: 'currentColor'
    },
    size: {
      type: [Number, String],
      default: 32
    }
  },
  computed: {
    svgDataUrl() {
      const path = iconPaths[this.name] || ''
      const svg = `<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1024 1024" width="${this.size}" height="${this.size}"><path fill="${this.color}" d="${path}"/></svg>`
      return `data:image/svg+xml;base64,${base64Encode(svg)}`
    }
  }
}
